package pack1;

public class explicit {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double d = 33;
		int i = (int)d;  // explicit type casting
		System.out.println(" d value = "+d);
		System.out.println(" i value = "+i);
	}
}


